import { useState, useEffect } from "react";

const App = () => {
  const [tasks, setTasks] = useState([]); // สถานะของ tasks
  const [task, setTask] = useState(""); // สถานะของ task

  // ฟังก์ชันดึงข้อมูล Todo เมื่อเริ่มต้น
  const fetchTodos = async () => {
    const response = await fetch('http://localhost:5000/api/todos');
    const data = await response.json(); // ดึงข้อมูล JSON จาก API
    setTasks(data); // อัปเดตสถานะ tasks
  };

  useEffect(() => {
    fetchTodos(); // เรียกใช้ฟังก์ชันเมื่อโหลดคอมโพเนนต์
  }, []);

  // ฟังก์ชันเพิ่ม Task
  const addTasks = async () => {
    if (task.trim() !== "") { // ตรวจสอบว่า task ไม่ว่าง
      const response = await fetch('http://localhost:5000/api/todos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title: task, completed: false }), // ส่ง title ไปยัง Back-End
      });

      if (response.ok) {
        fetchTodos(); // เรียกข้อมูลใหม่เมื่อเพิ่ม Task เสร็จ
        setTask(""); // ล้างฟิลด์กรอกข้อมูล
      } else {
        const errorData = await response.json();
        console.error("Error adding task:", errorData.error); // แสดงข้อความข้อผิดพลาด
      }
    } else {
      console.error("Task title cannot be empty"); // แสดงข้อความเมื่อ title ว่าง
    }
  };

  // ฟังก์ชันลบ Task
  const deleteTasks = async (id) => {
    await fetch(`http://localhost:5000/api/todos/${id}`, { // แก้ไข URL โดยใช้ backticks
      method: 'DELETE',
    });
    fetchTodos(); // เรียกข้อมูลใหม่เมื่อมีการลบ Task
  };

  // ฟังก์ชันสำหรับทำเครื่องหมายว่าเสร็จ
  const toggleComplete = async (id, completed) => {
    await fetch(`http://localhost:5000/api/todos/${id}`, { // แก้ไข URL โดยใช้ backticks
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ completed: !completed }), // ส่งข้อมูลที่อัปเดตไปยัง Back-End
    });
    fetchTodos(); // เรียกข้อมูลใหม่เมื่อมีการอัปเดตสถานะ
  };

  return (
    <div className="flex flex-col items-center">
      <h1 className="text-4xl m-16 font-bold">Simple Todo App</h1>
      <div className="p-6">
        <input
          className="bg-slate-100 rounded-md p-4 m-4"
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)} // ตั้งค่า task
          placeholder="Create a new todo"
        />
        <button
          onClick={addTasks} // เรียกใช้ฟังก์ชัน addTasks เมื่อคลิก
          className="bg-green-500 text-white p-3 m-3 rounded-md font-bold hover:bg-green-600"
        >
          Add Tasks
        </button>
      </div>
      <div>
        {tasks.length > 0 ? (
          <ul>
            {tasks.map((item) => (
              <div className="flex bg-slate-100 m-4 py-4 pl-12 pr-4 rounded-md" key={item.id}>
                <li className={`self-center font-semibold pr-10 mr-6 grow ${item.completed ? 'line-through' : ''}`}>
                  {item.title}
                </li>
                <button
                  onClick={() => toggleComplete(item.id, item.completed)} // ทำเครื่องหมายว่าเสร็จ
                  className="bg-green-500 text-white p-2 mx-1 rounded-md font-bold hover:bg-green-600"
                >
                  {item.completed ? "Undo" : "Done"}
                </button>
                <button
                  onClick={() => deleteTasks(item.id)} // ลบ Task
                  className="bg-red-500 text-white p-2 mx-1 rounded-md font-bold hover:bg-red-600"
                >
                  Delete
                </button>
              </div>
            ))}
          </ul>
        ) : (
          <div>
            <p>No Task Found</p> {/* แสดงข้อความเมื่อไม่มี Task */}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
